mapper.select:
SELECT COUNT(*)
FROM LOGINMEMBER
WHERE ID=#{id}

SELECT COUNT(*)
FROM LOGINMEMBER
WHERE NICKNAME=#{nickname}

SELECT COUNT(*)
FROM LOGINMEMBER
WHERE EMAIL=#{email}

SELECT *
FROM LOGINMEMBER
WHERE ID=#{id} AND PWD=#{pwd}

mapper.update:
UPDATE LOGINMEMBER
SET NAME=#{name}, NICKNAME=#{nickname}, AGE=#{age}, GENDER=#{gender}, EMAIL=#{email}
WHERE ID=#{id}

UPDATE LOGINMEMBER
SET PWD=#{pwd}
WHERE ID=#{id}

UPDATE LOGINMEMBER
SET PWD=#{pwd}
WHERE ID=#{id}
